
let img;
let endTime;
const snd = new Audio("data:audio/wav;base64,");
const alarmSound = new Audio("Alarm04.wav");

async function initializeTimer(minute) {
    endTime = new Date().getTime() / 60000 + minutes;
    const p = new Promise(resolve => {
        img = new Image();
        img.onload = resolve;
        img.src = "pizza-320.png";
    });
    await p;
    drawClock(minute);
}

function setTimer(mouseEvent) {
        let dX = mouseEvent.offsetX - canvas.width / 2;
        let dY = mouseEvent.offsetY - canvas.height / 2;
        let rad = Math.atan2(dX,-dY) ;
        minutes = rad2Min(rad);
        console.log("SET MINUTES: ",minutes);
        endTime = new Date().getTime() / 60000 + minutes ;
        syncUpdate();
        updateTimer();
    }

/*
Sync the unique timer value on the server with the endtime of this timer
*/
function syncUpdate() {
    fetch("http://www.fotoni.it/cgi-bin/timer?endMinutes="+endTime);
}

/*
Syncronize this timer with the unique timer value held on the server
 */
function sync() {
    fetch("http://www.fotoni.it/cgi-bin/timer")
        .then(response => {
            return response.json();
        })
        .then(data => {
            console.log(data);
            let endMinutes = Number.parseFloat(data);
            if (endMinutes != NaN && endTime != endMinutes) {
                endTime = endMinutes;
                minutes = endTime - (new Date().getTime() / 60000 );
                console.log("RESYNC: ",data);
                console.log("END: ",endMinutes);
                console.log("MINUTES: ",minutes);
                updateTimer();
            }
        });
}


function updateTimer() {
    if (minutes <= 0.1) {
        console.log("TEMPO SCADUTO!!!");
        minutes = 0;
        drawClock(0);
        alarmSound.play();
        // document.location.href = 'https://youtu.be/dQw4w9WgXcQ';
    } else {
        let now = new Date().getTime() / 60000;
        minutes = (endTime - now) ;
        if(minutes < 1)
            beep();
        drawClock(minutes);
    }
}

function min2Rad(min) {
    return (min-15) * Math.PI / 30;
}

function rad2Min(rad) {
    let min = Math.round(rad * 30 / Math.PI ) ;
    return min < 0 ? min+60 : min;
}

async function beep() {
    await snd.play();
}


function drawClock(minute) {
    // if(!pizzaIsReady) { return }
    const ctx = canvas.getContext('2d');
    let radius = canvas.height / 2.5;
    let centerx = canvas.width / 2;
    let centery = canvas.height / 2;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineWidth = 3;
    let pattern = ctx.createPattern(img,"no-repeat");

    // Draw a line every minute
    ctx.beginPath();
    ctx.strokeStyle = "#AAA";
    for(m = 0; m<60 ; m++) {
        let alpha = min2Rad(m);
        ctx.moveTo(centerx + radius * Math.cos(alpha), centery + radius * Math.sin(alpha));
        ctx.lineTo(centerx + 1.1*radius * Math.cos(alpha), centery + 1.1*radius * Math.sin(alpha));
    }
    ctx.stroke();
    ctx.closePath();

    // Draw a bold line every 5 minutes
    ctx.beginPath();
    ctx.strokeStyle = "#000";
    for(m = 0; m<60 ; m+=5) {
        let alpha = min2Rad(m);
        ctx.moveTo(centerx + radius * Math.cos(alpha), centery + radius * Math.sin(alpha));
        ctx.lineTo(centerx + 1.2*radius * Math.cos(alpha), centery + 1.2*radius * Math.sin(alpha));
    }
    ctx.stroke();
    ctx.closePath();

    // Draw the minutes sector
    ctx.beginPath();
    ctx.fillStyle = pattern;
    ctx.moveTo(centerx,centery);
    ctx.arc(centerx,centery,radius,min2Rad(0),min2Rad(minute));
    ctx.lineTo(centerx,centery);
    ctx.closePath();
    ctx.fill();
}

